import time
import pygame
def drawGrid(screen):
    screen.fill((210, 251, 255))
    for i in range(len(grid)):
        for j in range(len(grid[i])):
            color = (255, 255, 255)
            if (grid[i][j] == -1):
                color = (183, 97, 34)
            elif (grid[i][j] == 0):
                color = (26, 26, 28)
            elif (grid[i][j] == 1):

                if(oxyGrid[i][j]==1):
                    color = (18, 162, 255)
                    #color = (187, 249, 255)
                else:
                    color = (242, 224, 189)
            elif (grid[i][j] == 2):
                color = (31, 239, 255)
            pygame.draw.rect(screen, color, [i * boxX, j * boxY, boxX, boxY])
    pygame.display.flip()
    pygame.time.Clock().tick(60)


def spread():
    global toLook,toLookAfter
    counter =0

    for cell in (toLook):

        for direction in range(1,5):
            targetPosX = cell[0]
            targetPosY = cell[1]
            if (direction == 1):  # up
                targetPosY -= 1
            elif (direction == 2):  # down
                targetPosY += 1
            elif (direction == 3):  # left
                targetPosX -= 1
            elif (direction == 4):  # right
                targetPosX += 1
            if (targetPosX >= 0 and targetPosX < gridSize and targetPosY >= 0 and targetPosY < gridSize):

                if(grid[targetPosX][targetPosY]==1 and oxyGrid[targetPosX][targetPosY]==0):
                    oxyGrid[targetPosX][targetPosY] =1
                    toLookAfter.append([targetPosX,targetPosY])
                    counter +=1


    toLook=toLookAfter
    toLookAfter=[]
    return counter
